const getLevels = () => {
  const levelTypes = [{
    levelType: 0,
    task: `Угадайте для каждого изображения фото или рисунок?`,
    options: [{
      image: `http://placehold.it/468x458`,
      type: `photo`,
      answers: {
        photo: true,
        paint: false
      }
    }, {
      image: `http://placehold.it/468x458`,
      type: `paint`,
      answers: {
        photo: false,
        paint: true
      }
    }]
  }];
  let levels = [];
  const getRandomInt = (min, max) => {
    return Math.floor(Math.random() * (max - min)) + min;
  };
  for (let i = 0; i < 10; i++) {
    levels.push(levelTypes[getRandomInt(0, 1)]);
  }
  return levels;
};

